package com.mongootech.mongyan.bean.netbean/** * list	object []非必须item 类型: objectaddress	string必须影院地址province	string必须省份city	string必须城市location	object必须lon	string非必须经度lat	string非必须维度id	string必须影院IDtitle	string必须影院名称sort	string必须距离total	string非必须总条数 */class SearchCinemas:ArrayList<SearchCinemas.SearchCinema>() {    data class SearchCinema(var list: List<CinemaInfo>, var total: String)    data class CinemaInfo(        var address: String,        var province: String,        var city: String,        var id: String,        var title: String,        var sort: String,        var location: LocationInfo,        var planList:List<PlanList>    )    data class LocationInfo(        var lon: String,        var lat: String    )    /**     * movieName	string    必须    影视名字    pId	string    必须    排片标识    movieCode	string    必须    影视编码    cinemaId	string    必须    影院ID    appCode	string    必须    排片编码    startTime	string    必须    开始时间    endTime	string    必须    结束时间    language	string    必须    语言    type	string    必须    类型     */    data class PlanList(        var movieName: String,        var pId: String,        var movieCode: String,        var cinemaId: String,        var appCode: String,        var startTime: String,        var endTime: String,        var language: String,        var type: String    )}