package com.mongootech.mongyan.bean.otherbeanimport android.text.TextUtilsclass PayResult {    /**     * @return the resultStatus     */    var resultStatus: String? = null    /**     * @return the result     */    var result: String? = null    /**     * @return the memo     */    var memo: String? = null    constructor(rawResult: Map<String, String>?){        if (rawResult == null) {            return        }        for (key in rawResult!!.keys) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult[key]            } else if (TextUtils.equals(key, "result")) {                result = rawResult[key]            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult[key]            }        }    }    override fun toString(): String {        return ("resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}")    }}